import React, { Component } from 'react';
import Map from './Map';

class Menu extends Component {
    constructor(props){
        super(props);
        this.state = {
            arr1 : [12, 13, 14, 15, 16],
            elements : [],
            currentElement : '',
            funcName : ''
        }
    }

    renderMapFunction = () => {           
        return <Map array = {this.state.arr1} name = {this.state.funcName}/>
    }

    setInputField = (event) => {
        this.setState({
            currentElement: parseInt(event.target.value)
        })
    }

    onAddElement = () => {
        this.setState({
            elements: [...this.state.elements, this.state.currentElement]
        })
        this.setState({
            currentElement: '',
            funcName : 'Map Function'
        })
    }

    render() {
        console.log(this.state.elements);
        return ( 
            <div>
                <h1>Higher Order Functions</h1>
                <h6>Enter Array : </h6>
                <div>
                    <input type='text' placeholder='Add element  ' value={this.state.currentElement} onChange={this.setInputField}></input>
                    <button className='btn btn-primary' onClick={this.onAddElement}>Add Element</button>
                </div>
                <button className='btn btn-secondary' onClick={this.renderMapFunction}>Call Map()</button>

            </div>
         );
    }
}

export default Menu;
