import React, { Component } from 'react';
import Map from './Map';

class Menu extends Component {
    constructor(props){
        super(props);
        this.state = {
            arr1 : [12, 13, 14, 15, 16],
            elements : []
        }
    }

    renderMapFunction = () => {        
        return(
            <div>
                <Map array = {this.state.arr1} name = 'Map Function'/>
            </div>
        )
    }

    setInputField = (event) => {
        this.setState({
            elements: this.state.elements.push(event.target.value)
        })
    }
    render() { 
        return ( 
            <div>
                <h1>Higher Order Functions</h1>
                <h6>Enter Array : </h6>
                <div>
                    <input type='text' placeholder='Add element : ' onChange={this.setInputField()}></input>
                </div>
                {/* <button className='btn btn-secondary' onClick={this.renderMapFunction}>Call Map()</button> */}
                {/* {this.renderMapFunction()} */}
            </div>
         );
    }
}

export default Menu;
