import React, { Component } from 'react';

class Map extends Component {
    constructor(props){
        super(props);
        this.state = {
            numberToBeAdded : 0
        }
    }

    mapFunction = () => {
        return this.props.array.map((arr,index) =>{
            return arr+parseInt(this.state.numberToBeAdded)+'\n'
        })
    }

    static getDerivedStateFromProps(nextProps, prevState){
        console.log(nextProps);
    }

    updateAddtionNumber = (event) => {
        this.setState({
            numberToBeAdded : event.target.value
        })
     }

    render() {
        return ( 
            <div>
                <input type='text' placeholder='Enter number to add' onChange={this.updateAddtionNumber}></input>
                <div>
                    {'Function name : '+this.props.name}
                </div>
                <div>
                    {this.mapFunction()}
                </div>
            </div>
         );
    }
}

export default Map;
